insert into recipe (title, time, level, portions, steps, source, image_source, is_vege, category_id) values
('Pomidorowa z makaronem', 20, 'EASY', 4, 'Krok 1: Pokrój pietruszkę lub koperek. Ugotuj makaron wg instrukcji na opakowaniu. Przesmaż cebulę z czosnkiem (lekko zmiażdżonym) na oliwie. & Krok 2: Dodaj pomidory i gotuj przez 5 min. Przetrzyj pomidory przez sito. & Krok 3: Dodaj 1 l wody i 2 kostki rosołowe. Gotuj przez 10 min. Podawaj zupę z makaronem posypaną koperkiem lub pietruszką.',
