insert into recipe (title, time, level, portions, steps, source, image_source, is_vege, category_id) values
('Ciasto czekoladowe z bananami', 90, 'EASY', 6, 'Krok 1: Banany przekładamy do miski. Dodajemy jogurt naturalny. Rozgniatamy całość widelcem. & Krok 2: Masło roztapiamy w rondelku. Dodajemy czekoladę oraz mleko, roztapiamy. Studzimy. & Krok 3: Jajka ubijamy mikserem. Stopniowo dodajemy połowę mąki, kakao i proszku do pieczenia. & Krok 4: Następnie wlewamy roztopione masło z czekoladą, wsypujemy resztę mąki, proszku do pieczenia i kakao oraz rozgniecione z jogurtem banany. Ponownie miksujemy. & Krok 5: Piekarnik nagrzewamy do 180 stopni. Keksówkę smarujemy odrobiną masła i obsypujemy mąką. Masę czekoladowo-bananową przelewamy do keksówki. Pieczemy ok. 60 minut.',
 'https://www.przepisy.pl/przepis/ciasto-czekoladowe-z-bananami',
