insert into recipe (title, time, level, portions, steps, source, image_source, is_vege, category_id) values
('Panna cotta', 30, 'EASY', 4, 'Krok1: Do miseczki wlej 3 łyżki ciepłej wody i dodaj żelatynę. Dokładnie rozmieszaj i odstaw na 15 minut.& Krok 2: Do garnka wlej mleko i śmietankę, dodaj cukier puder i waniliowy. Całość rozmieszaj i gotuj, w momencie zagotowania zestaw z gazu.& Krok 3: Do garnka dodaj żelatynę i dokładnie wymieszaj. Całość przelej do 4 filiżanek i odstaw do lodówki na 4 godziny do ochłodzenia.& Krok 4: W garnku podgrzej maliny z odrobiną cukru pudru.& Krok 5: Dodaj odrobinę wody i duś przez 10 minut.& Krok 5: Podanym musem polej gotowy deser.',
 'https://fajnegotowanie.pl/przepisy/panna-cotta/',
