insert into recipe (title, time, level, portions, steps, source, image_source, is_vege, category_id) values
('Nuggetsy', 80, 'EASY', 2, 'Krok 1: W misce wymieszaj kefir z jajkiem, mąką pszenną, 1 łyżeczką słodkiej papryki, 1/3 łyżeczką soli, pieprzu i ostrej papryki.& Krok 2: Pierś z kurczaka umyj i pokrój w równe kawałki, mniej więcej 3 cm długości. & Krok 3: Kawałki kurczaka przełóż do kefiru.& Krok 4: Płatku kukurydziane możesz lekko rozdrobnić w moździerzu, torebce foliowej, zmielić w rozdrabniaczu lub lekko pokruszyć w dłoniach.& Krok 5: Blachę do pieczeni wyłóż papierem do pieczenia.& Krok 6: Kawałki kurczaka obtocz w płatkach i układaj na blaszce. & Krok 7: Wierzch kawałków kurczaka posmaruj delikatnie olejem.& Krok 8: Nuggetsy piecz 15 minut w temperaturze 200 stopni.',
